import React, {useState} from "react";
import {loadStripe} from "@stripe/stripe-js";
import {Elements} from "@stripe/react-stripe-js";
import {BrowserRouter as Router, Route, Routes} from 'react-router-dom';
import CheckoutForm from "./CheckoutForm";
import CompletePage from "./CompletePage";
import "./App.css";

// Make sure to call loadStripe outside of a component’s render to avoid
// recreating the Stripe object on every render.
// This is a public sample test API key.
// Don’t submit any personally identifiable information in requests made with this key.
// Sign in to see your own test API key embedded in code samples.
const stripePromise = loadStripe("pk_test_51Q801tRpxgEVE539pBARC3D84A8MPZskWP4cyVuXZZOWGDVFmD2YF7pfZArK7jYymrTSZ4aGE2hlxKe0LLUUaHrI00NC71Qgaz");

export default function App() {
    const [clientSecret, setClientSecret] = useState("");
    const [dpmCheckerLink, setDpmCheckerLink] = useState("");
    const [order, setOrder] = useState("");
    const [isModalOpen, setIsModalOpen] = useState(true);

    const handleModalSubmit = () => {
        setIsModalOpen(false);
        call();
    };


    const call = (() => {
        console.log(clientSecret)
        // Create PaymentIntent as soon as the page loads
        // fetch(`http://localhost:8080/api/v1/payments/${order}`, {
        fetch(`http://localhost:8080/api/v1/payments/faec94ca-8583-4380-b0b5-6c6458abb220`, {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(),
        })
            .then((res) => res.json())
            .then((data) => {
                console.log(data.data)
                setClientSecret(data.data.clientSecret);
                // [DEV] For demo purposes only
                setDpmCheckerLink(data.data.dpmCheckerLink);
            });
    }, []);

    const appearance = {
        theme: 'stripe',
    };
    // Enable the skeleton loader UI for optimal loading.
    const loader = 'auto';

    return (
        <Router>
            <div className="App">
                <Routes>
                    <Route path = "/checkout" element={
                clientSecret ? (
                        <Elements options={{clientSecret, appearance, loader}} stripe={stripePromise}>
                            <CheckoutForm dpmCheckerLink={dpmCheckerLink} />
                        </Elements>
                    ) : (
                    <div className="modal">
                        <div className="modal-content">
                            <h2>상품 ID 입력</h2>
                            <input
                                type="text"
                                placeholder="상품 ID를 입력하세요"
                                // faec94ca-8583-4380-b0b5-6c6458abb220
                                value={order}
                                onChange={(e) => setOrder(e.target.value)}
                            />
                            <button onClick={handleModalSubmit}>확인</button>
                        </div>
                    </div>
                    )
                } />
                    <Route path = "/complete" element={<CompletePage />} />
                </Routes>
            </div>
        </Router>
    );
}
