import React, {useState} from "react";
import {loadStripe} from "@stripe/stripe-js";
import {Elements} from "@stripe/react-stripe-js";
import {BrowserRouter as Router, Route, Routes, useNavigate} from 'react-router-dom';
import CheckoutForm from "./CheckoutForm";
import CompletePage from "./CompletePage";
import Order from "./Order";

import "./App.css";

// Make sure to call loadStripe outside of a component’s render to avoid
// recreating the Stripe object on every render.
// This is a public sample test API key.
// Don’t submit any personally identifiable information in requests made with this key.
// Sign in to see your own test API key embedded in code samples.
const stripePromise = loadStripe("pk_test_51Q801tRpxgEVE539pBARC3D84A8MPZskWP4cyVuXZZOWGDVFmD2YF7pfZArK7jYymrTSZ4aGE2hlxKe0LLUUaHrI00NC71Qgaz");

export default function App() {
    const [clientSecret, setClientSecret] = useState("");
    const [dpmCheckerLink, setDpmCheckerLink] = useState("");
    const navigate = useNavigate();

    const appearance = {
        theme: 'stripe',
    };
    // Enable the skeleton loader UI for optimal loading.
    const loader = 'auto';
    const moveToOrder = () => {
        navigate('/order');
    }
    return (
        <Router>
            <button onClick={moveToOrder}>주문하기</button>
            {clientSecret && (
                <Elements options={{clientSecret, appearance, loader}} stripe={stripePromise}>
                    <Routes>
                        <Route path="/order" element={<Order/>}/>
                        <Route path="/checkout" element={<CheckoutForm dpmCheckerLink={dpmCheckerLink}/>}/>
                        <Route path="/complete" element={<CompletePage/>}/>
                    </Routes>
                </Elements>
            )}
        </Router>
    )
}
